import { useState, useEffect } from "react";

function App() {
  const [contador, setContador] = useState<number>(() => {
    // Inicializa lendo do localStorage
    const valorSalvo = localStorage.getItem("contador");
    return valorSalvo ? parseInt(valorSalvo, 10) : 0;
  });

  // Sempre que o contador mudar, salvar no localStorage
  useEffect(() => {
    localStorage.setItem("contador", contador.toString());
  }, [contador]);

  return (
    <div style={{ textAlign: "center", marginTop: "50px" }}>
      <h1>Contador Persistente ðŸš€</h1>
      <p>Valor atual: {contador}</p>

      <button onClick={() => setContador(contador + 1)}>+1</button>
      <button onClick={() => setContador(contador - 1)}>-1</button>
      <button onClick={() => setContador(0)}>Resetar</button>
    </div>
  );
}

export default App;
