import { useState, useEffect } from "react";

type ContadorProps = {
  titulo: string;
};

export function Contador({ titulo }: ContadorProps) {
  // Estado inicial lendo do localStorage
  const [contador, setContador] = useState<number>(() => {
    const dadosSalvos = localStorage.getItem(titulo);
    if (dadosSalvos) {
      const obj = JSON.parse(dadosSalvos);
      return obj.contador ?? 0;
    }
    return 0;
  });

  const [historico, setHistorico] = useState<string[]>(() => {
    const dadosSalvos = localStorage.getItem(titulo);
    if (dadosSalvos) {
      const obj = JSON.parse(dadosSalvos);
      return obj.historico ?? [];
    }
    return [];
  });

  // Sempre que contador ou histÃ³rico mudarem, salvar no localStorage
  useEffect(() => {
    const dados = { contador, historico };
    localStorage.setItem(titulo, JSON.stringify(dados));
  }, [contador, historico, titulo]);

  const registrarAcao = (acao: string, novoValor: number) => {
    setContador(novoValor);
    setHistorico((prev) => [...prev, acao]);
  };

  // ðŸ‘‰ IMPORTANTE: esse return precisa estar DENTRO da funÃ§Ã£o
  return (
    <div
      style={{
        background: "linear-gradient(135deg, #fdfbfb, #ebedee)",
        borderRadius: "16px",
        padding: "30px",
        textAlign: "center",
        boxShadow: "0 6px 18px rgba(0,0,0,0.1)",
      }}
    >
      <h2 style={{ marginBottom: "15px", color: "#333" }}>{titulo}</h2>
      <p style={{ fontSize: "28px", fontWeight: "bold", margin: "10px 0" }}>
        {contador}
      </p>

      <div style={{ display: "flex", justifyContent: "center", gap: "15px" }}>
        <button
          onClick={() => registrarAcao("+1", contador + 1)}
          style={{
            backgroundColor: "#4caf50",
            color: "white",
            border: "none",
            borderRadius: "8px",
            padding: "10px 20px",
            cursor: "pointer",
          }}
        >
          +1
        </button>
        <button
          onClick={() => registrarAcao("-1", contador - 1)}
          style={{
            backgroundColor: "#f44336",
            color: "white",
            border: "none",
            borderRadius: "8px",
            padding: "10px 20px",
            cursor: "pointer",
          }}
        >
          -1
        </button>
        <button
          onClick={() => registrarAcao("reset", 0)}
          style={{
            backgroundColor: "#2196f3",
            color: "white",
            border: "none",
            borderRadius: "8px",
            padding: "10px 20px",
            cursor: "pointer",
          }}
        >
          Resetar
        </button>
      </div>

      <h3 style={{ marginTop: "20px", color: "#444" }}>HistÃ³rico:</h3>
      <ul style={{ listStyle: "none", padding: 0 }}>
        {historico.map((acao, index) => (
          <li
            key={index}
            style={{
              color:
                acao === "+1"
                  ? "green"
                  : acao === "-1"
                  ? "red"
                  : "#2196f3",
              fontWeight: "bold",
            }}
          >
            {acao}
          </li>
        ))}
      </ul>
    </div>
  );
}
