      <button onClick={incrementar}>+1</button>
      <button onClick={decrementar}>-1</button>
      <button onClick={resetar}>Resetar</button>

      <h2>HistÃ³rico de aÃ§Ãµes:</h2>
      <ul>
        {historico.map((acao, index) => (
          <li key={index}>{acao}</li>
        ))}
      </ul>
    </div>
  );
}

export default App;
