import { useState, useEffect } from "react";

type ContadorProps = {
  titulo: string;
};

export function Contador({ titulo }: ContadorProps) {
  const [contador, setContador] = useState<number>(() => {
    const valorSalvo = localStorage.getItem(titulo);
    return valorSalvo ? parseInt(valorSalvo, 10) : 0;
  });

  useEffect(() => {
    localStorage.setItem(titulo, contador.toString());
  }, [contador, titulo]);

  return (
    <div
      style={{
        background: "#f9f9f9",
        borderRadius: "12px",
        padding: "20px",
        textAlign: "center",
        boxShadow: "0 4px 8px rgba(0,0,0,0.1)",
      }}
    >
      <h2>{titulo}</h2>
      <p style={{ fontSize: "20px", fontWeight: "bold" }}>Valor: {contador}</p>

      <div style={{ display: "flex", justifyContent: "center", gap: "10px" }}>
        <button onClick={() => setContador(contador + 1)}>+1</button>
        <button onClick={() => setContador(contador - 1)}>-1</button>
        <button onClick={() => setContador(0)}>Resetar</button>
      </div>
    </div>
  );
}
