        <button className="toggle-btn" onClick={() => setDarkMode(!darkMode)}>
          {darkMode ? "‚òÄÔ∏è Claro" : "üåô Escuro"}
        </button>
      </header>

      <div className="grid">
        <Contador titulo="Cliques" />
        <Contador titulo="Visitas" />
        <Contador titulo="Tarefas feitas" />
      </div>
    </div>
  );
}

export default App;
