import { useState, useEffect } from "react";

type ContadorProps = {
  titulo: string;
};

export function Contador({ titulo }: ContadorProps) {
  const [contador, setContador] = useState<number>(() => {
    const valorSalvo = localStorage.getItem(titulo);
    return valorSalvo ? parseInt(valorSalvo, 10) : 0;
  });

  useEffect(() => {
    localStorage.setItem(titulo, contador.toString());
  }, [contador, titulo]);

  return (
    <div
      style={{
        background: "linear-gradient(135deg, #fdfbfb, #ebedee)",
        borderRadius: "16px",
        padding: "30px",
        textAlign: "center",
        boxShadow: "0 6px 18px rgba(0,0,0,0.1)",
        transition: "transform 0.2s",
      }}
    >
      <h2 style={{ marginBottom: "15px", color: "#333" }}>{titulo}</h2>
      <p style={{ fontSize: "28px", fontWeight: "bold", margin: "10px 0" }}>
        {contador}
      </p>

      <div style={{ display: "flex", justifyContent: "center", gap: "15px" }}>
        <button
          onClick={() => setContador(contador + 1)}
          style={{
            backgroundColor: "#4caf50",
            color: "white",
            border: "none",
            borderRadius: "8px",
            padding: "10px 20px",
            cursor: "pointer",
            fontSize: "16px",
          }}
        >
          +1
        </button>
        <button
          onClick={() => setContador(contador - 1)}
          style={{
            backgroundColor: "#f44336",
            color: "white",
            border: "none",
            borderRadius: "8px",
            padding: "10px 20px",
            cursor: "pointer",
            fontSize: "16px",
          }}
        >
          -1
        </button>
        <button
          onClick={() => setContador(0)}
          style={{
            backgroundColor: "#2196f3",
            color: "white",
            border: "none",
            borderRadius: "8px",
            padding: "10px 20px",
            cursor: "pointer",
            fontSize: "16px",
          }}
        >
          Resetar
        </button>
      </div>
    </div>
  );
}
